package com.octoperf.kraken.security.decoder.spring;

import com.google.common.collect.ImmutableList;
import com.octoperf.kraken.Application;
import com.octoperf.kraken.security.decoder.api.TokenDecoder;
import com.octoperf.kraken.security.entity.token.KrakenRole;
import com.octoperf.kraken.security.entity.token.KrakenTokenUser;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.test.context.junit.jupiter.SpringExtension;

import java.io.IOException;
import java.time.Instant;

import static org.assertj.core.api.Assertions.assertThat;

@ExtendWith(SpringExtension.class)
@SpringBootTest(classes = Application.class)
public class SpringTokenDecoderTest {

  @Autowired
  TokenDecoder decoder;

  @Test
  public void shouldDecodeAccessToken() throws IOException {
    final var user = decoder.decode("eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJZb19LT2IzeUZncGlzM05tT1F2OFE0N2ZJQlltbkpsZEtlRE1LQ1lBQThjIn0..JzhagQ27346NngHPFD7aclVxXlJupCLeXmNNE8Qth8CvlsNhdHilJ38NYph9j64mWLMrW-bol0Pgf3xZ5WwVNbCnjpKIZnFxhcYiZMgvW3fM9DepC7UqyrEUl04Y39Y72bODjNDhE6CPrD1tkw4BUJts-EtbA6CawwGofkBXUynTuFoHhTKrkNRIdTN_7rvktHPOm3hFl5pVHoWCLLIOScCf2FK58mj4FXLN__rOgtEVIUAXUKrDgL3CsHzDsmKrrylOdzo4TLB04nptJRBUlSNdC1Qi8gNdNX3_54Aeq8O2tflbw6ufUR3LVVA_p7Ua6s0bvyuPFMJ7XCnv0AtkMw");
    assertThat(user)
        .isEqualTo(
            KrakenTokenUser.builder()
                .username("contact@octoperf.com")
                .email("contact@octoperf.com")
                .userId("2e44ffae-111c-4f59-ae2b-65000de6f7b7")
                .roles(ImmutableList.of(KrakenRole.USER))
                .groups(ImmutableList.of("/default-kraken"))
                .currentGroup("/default-kraken")
                .issuedAt(Instant.ofEpochSecond(1588251492))
                .expirationTime(Instant.ofEpochSecond(1588251792))
                .build()
        );
  }

  @Test
  public void shouldDecodeRefreshToken() throws IOException {
    final var user = decoder.decode("eyJhbGciOiJIUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICI4MGY1ZTAyMS04M2MxLTQzNzUtOWE4YS1kNTFlNzI4ZDQ5MWQifQ..JNlM-t0ST59ZHO1Ua9yBqmyqKhSEw6lpJOs4RvCS6ts");
    assertThat(user)
        .isEqualTo(
            KrakenTokenUser.builder()
                .username("")
                .email("")
                .userId("2e44ffae-111c-4f59-ae2b-65000de6f7b7")
                .roles(ImmutableList.of())
                .groups(ImmutableList.of())
                .currentGroup("")
                .issuedAt(Instant.ofEpochSecond(1588251492))
                .expirationTime(Instant.ofEpochSecond(1588253292))
                .build()
        );
  }

  @Test
  public void shouldFailToDecodeMalformattedToken() {
    Assertions.assertThrows(IllegalArgumentException.class, () -> {
      decoder.decode("abc");
    });
  }

  @Test
  public void shouldFailToDecodeMalformattedJSon() {
    Assertions.assertThrows(IOException.class, () -> {
      decoder.decode("asd.bqsdqs.cqsdqsd");
    });
  }


  @Test
  public void shouldFailToDecodeGroupMismatch() {
    Assertions.assertThrows(IllegalArgumentException.class, () -> {
      decoder.decode("eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJZb19LT2IzeUZncGlzM05tT1F2OFE0N2ZJQlltbkpsZEtlRE1LQ1lBQThjIn0..EL-VlLgpu7uzil7Ucy6R02cGXcrxkv-mq53jSDqgRYvZEwECrYf-IYzN2vJlSyxeu6c5QWogmtngaisMnMBmSKa4mnHCw0dtp5ie_OosEsWN7HDkAMNMJLfCfIq7sgGZvpFl2muSc6TNbwhM4OSQSOC_jtQdQNhHggQ1tMOXpJ5rWGCYizMyblnDrjUB0udYsX1eUZYUnMms_FX8YIrxw9Yq72y0YTXPdwEvaDX_u28gcSrnMbgHdETsEIQkmtVWQ2rN-BEQeVk50bpiWnf5OKS2T7XoK9R2Mz1z__ycSh5BxKiNY6yyz-9sdlRALNEQbKRMGhvcq5reIIJwZ1rWxg\n");
    });
  }
}